var request=require('request');
var cheerio=require('cheerio')
module.exports=function(url,res){
	request(url,function(err,resp,body){
		if(!err){
			$=cheerio.load(body,{
				ignoreWhitespace: true,
				xmlMode: true,
				lowerCaseTags: false
			});
			console.log($('item').length);
			res.send($('item').eq(1).html())		
		}
	});
}	