
module.exports=function(app){
	/*el objeto app recibe como parametro una ruta ('/' ..... '/larepublica/all'), la cual
	carga un feed diferente, si quieren agregar nuevos feeds, solo agreguen una ruta y
	la url de algun feed que quieran procesar*/
	app.get('/',function(req,res){
		res.render('index',{title:'Feed'});
	});
	app.get('/larepublica/all',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/rss',res);
	})
	app.get('/larepublica/espectaculos',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/espectaculos/espectaculos',res);
	})
	app.get('/larepublica/fulbol-peruano',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/deportes/futbol-peruano',res);
	})	
	app.get('/larepublica/ligas-internacionales',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/deportes/ligas-internacionales',res);
	})
	app.get('/larepublica/sexualidad',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/sexualidad/sexualidad',res);
	})
	app.get('/larepublica/empresa',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/empresa/empresa',res);
	})
	app.get('/larepublica/salud',function(req,res){
		require('App/Feed')('http://www.larepublica.pe/rss/salud/salud',res);
	})	
	app.get('/rpp/asia-pop',function(req,res){
		require('App/Feed')('http://www.rpp.com.pe/asia-pop-rss_869.xml',res);
	})	


/*-------	paginas scrapping				*/
/*
	esta no es una ruta cualquiera, aqui se hace un scraping ya que el parametro de carga
	(url), no es un feed si no una pagina web, es necesario tener conocimientos especiales
	para poder realizar este trabajo.
	Para editar este scraping debes ir a node_modules/App/Scrapping/index.js
*/
	app.get('/cine',function(req,res){
		var cine= require('App/Scrapping');
		cine.estrenos(res)
	});
	
}