var request=require('request');
var cheerio=require('cheerio');
exports.cinepapaya=function(res){	
	request('http://www.papaya.pe/',function(err,res,body){
		if(!err){
			$=cheerio.load(body);
			var estrenos=new Array();
			var $estrenos=$('.f_estreno').each(function(i,elem){
				estrenos[i]=$(this).attr('href');
			})
			res.send({
				imagen:estrenos
			})
		}
	});
}